import{_ as a,o as s,c as e,R as n}from"./chunks/framework.HLJ3dCim.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"backend_static_compilation.md","filePath":"backend_static_compilation.md","lastUpdated":1703620083000}'),t={name:"backend_static_compilation.md"},o=n(`<h2 id="将静态资源编译成go文件打包到可执行文件内" tabindex="-1">将静态资源编译成go文件打包到可执行文件内 <a class="header-anchor" href="#将静态资源编译成go文件打包到可执行文件内" aria-label="Permalink to &quot;将静态资源编译成go文件打包到可执行文件内&quot;">​</a></h2><p>原版教程来源:<a href="https://blog.enianteam.com/u/sun/content/211" target="_blank" rel="noreferrer">https://blog.enianteam.com/u/sun/content/211</a></p><blockquote><p>为了简化部署和减少出错的几率，将前端文件打包到可执行文件中，最终程序发布之后只有一个可执行文件</p></blockquote><h3 id="_1-安装" tabindex="-1">1. 安装 <a class="header-anchor" href="#_1-安装" aria-label="Permalink to &quot;1. 安装&quot;">​</a></h3><p>** 注意：<code>...</code>必须带上 **</p><div class="language-ssh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ssh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>go get github.com/go-bindata/go-bindata/...</span></span>
<span class="line"><span>go get github.com/elazarl/go-bindata-assetfs/...</span></span>
<span class="line"><span></span></span>
<span class="line"><span># go版本&gt;=1.17 使用intsall方式</span></span>
<span class="line"><span>go install -a -v github.com/go-bindata/go-bindata/...@latest</span></span>
<span class="line"><span>go install -a -v github.com/elazarl/go-bindata-assetfs/...@latest</span></span></code></pre></div><h3 id="_2-安装成功后将-gopath-bin-加入环境变量" tabindex="-1">2. 安装成功后将 <code>GOPATH/bin</code> 加入环境变量 <a class="header-anchor" href="#_2-安装成功后将-gopath-bin-加入环境变量" aria-label="Permalink to &quot;2. 安装成功后将 \`GOPATH/bin\` 加入环境变量&quot;">​</a></h3><p>参考各自系统环境变量配置即可</p><h3 id="_3-压缩静态文件-到-asset目录" tabindex="-1">3. 压缩静态文件 到 asset目录 <a class="header-anchor" href="#_3-压缩静态文件-到-asset目录" aria-label="Permalink to &quot;3. 压缩静态文件 到 asset目录&quot;">​</a></h3><p>以下命令在Windows的<code>powershell</code>可能会报错，可使用<code>cmd</code>执行</p><div class="language-ssh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ssh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span># 开发环境，并非真实将所有文件编译，修改静态文件可以及时生效</span></span>
<span class="line"><span>go-bindata-assetfs -debug -o=assets/bindata.go -pkg=assets static/... view/... # 多个</span></span>
<span class="line"><span>go-bindata-assetfs -debug -o=assets/bindata.go -pkg=assets assets/... </span></span>
<span class="line"><span></span></span>
<span class="line"><span># 正式环境，修改静态文件后需要重新编译</span></span>
<span class="line"><span>go-bindata-assetfs -o=assets/bindata.go -pkg=assets assets/...</span></span></code></pre></div><blockquote><p>正式环境需要 去掉<code>-debug</code></p></blockquote><h4 id="参考文章" tabindex="-1">参考文章 <a class="header-anchor" href="#参考文章" aria-label="Permalink to &quot;参考文章&quot;">​</a></h4><p>Go | Go 语言打包静态文件以及如何与Gin一起使用Go-bindata <a href="https://www.jianshu.com/p/a7f5885679ef" target="_blank" rel="noreferrer">https://www.jianshu.com/p/a7f5885679ef</a></p><p>[golang]Go内嵌静态资源go-bindata的安装及使用 <a href="https://www.cnblogs.com/landv/p/11577213.html" target="_blank" rel="noreferrer">https://www.cnblogs.com/landv/p/11577213.html</a></p>`,15),p=[o];function i(l,c,d,r,h,g){return s(),e("div",null,p)}const u=a(t,[["render",i]]);export{_ as __pageData,u as default};
